mapper.update:
UPDATE PEOPLE
SET	NAME = #{name}, BIRTH = #{birth}, NICKNAME = #{nickname}, ADDRESS = #{address}, PHONE = #{phone}, INTRO = #{intro}
WHERE PEOPLE_ID = #{peopleId}

mapper.select:
SELECT PEOPLE_ID, PASSWORD
FROM PEOPLE
WHERE PEOPLE_ID = #{peopleId}

SELECT PEOPLE_ID
FROM PEOPLE
WHERE NAME = #{name} and EMAIL = #{email}

SELECT *
FROM PEOPLE
WHERE PEOPLE_ID = #{peopleId} and NAME = #{name} and EMAIL = #{email}

mapper.update:
UPDATE PEOPLE
SET PASSWORD = #{password}
WHERE PEOPLE_ID = #{peopleId}

mapper.select:
SELECT PEOPLE_ID
FROM PEOPLE
WHERE PEOPLE_ID = #{peopleId}

SELECT EMAIL
FROM PEOPLE
WHERE EMAIL = #{email}

SELECT NICKNAME
FROM PEOPLE
WHERE NICKNAME = #{nickname}
